var reverseWords = function(s) {
    let i = 0, j = s.length - 1;

    while(i < j) {
        [s[i], s[j]] = [s[j], s[i]];
        i++;
        j--;
    }

    i = 0;
    while(i < s.length) {
        j = i;

        while(s[i] != ' ' && i < s.length) {
            i++;
        }

        k = i - 1;

        while(j <= k) {
            [s[k], s[j]] = [s[j], s[k]];
            j++;
            k--;
        }

        i++
    }
};
